`timescale 1 ns/10 ps  // time-unit = 1 ns, precision = 10 ps

module top_module_tb;

    // duration for each bit = 20 * timescale = 20 * 1 ns  = 20ns
    localparam period = 20;

    reg [1023:0] in;
    reg [7:0] sel;

    wire [3:0] out;


    integer mismatch_count;

    top_module UUT (.in(in), .sel(sel), .out(out));

    initial begin
        mismatch_count = 0;

        // Tick 0: Inputs = 1024', 8'b10000000, Generated = out, Reference = 4'b0101
        in = 1024'; sel = 8'b10000000; // Set input values
        #period;
        if (!(out === 4'b0101)) begin
            $display("Mismatch at index 0: Inputs = [%b, %b], Generated = [%b], Reference = [%b]", 1024', 8'b10000000, out, 4'b0101);
            mismatch_count = mismatch_count + 1;
        end

        else begin
            $display("Test 0 passed!");
        end

        // Tick 1: Inputs = 1024', 8'b01001111, Generated = out, Reference = 4'b1000
        in = 1024'; sel = 8'b01001111; // Set input values
        #period;
        if (!(out === 4'b1000)) begin
            $display("Mismatch at index 1: Inputs = [%b, %b], Generated = [%b], Reference = [%b]", 1024', 8'b01001111, out, 4'b1000);
            mismatch_count = mismatch_count + 1;
        end

        else begin
            $display("Test 1 passed!");
        end

        // Tick 2: Inputs = 1024', 8'b10000101, Generated = out, Reference = 4'b1010
        in = 1024'; sel = 8'b10000101; // Set input values
        #period;
        if (!(out === 4'b1010)) begin
            $display("Mismatch at index 2: Inputs = [%b, %b], Generated = [%b], Reference = [%b]", 1024', 8'b10000101, out, 4'b1010);
            mismatch_count = mismatch_count + 1;
        end

        else begin
            $display("Test 2 passed!");
        end

        // Tick 3: Inputs = 1024', 8'b00001110, Generated = out, Reference = 4'b1101
        in = 1024'; sel = 8'b00001110; // Set input values
        #period;
        if (!(out === 4'b1101)) begin
            $display("Mismatch at index 3: Inputs = [%b, %b], Generated = [%b], Reference = [%b]", 1024', 8'b00001110, out, 4'b1101);
            mismatch_count = mismatch_count + 1;
        end

        else begin
            $display("Test 3 passed!");
        end

        // Tick 4: Inputs = 1024', 8'b11000111, Generated = out, Reference = 4'b1010
        in = 1024'; sel = 8'b11000111; // Set input values
        #period;
        if (!(out === 4'b1010)) begin
            $display("Mismatch at index 4: Inputs = [%b, %b], Generated = [%b], Reference = [%b]", 1024', 8'b11000111, out, 4'b1010);
            mismatch_count = mismatch_count + 1;
        end

        else begin
            $display("Test 4 passed!");
        end

        // Tick 5: Inputs = 1024', 8'b01100001, Generated = out, Reference = 4'b1011
        in = 1024'; sel = 8'b01100001; // Set input values
        #period;
        if (!(out === 4'b1011)) begin
            $display("Mismatch at index 5: Inputs = [%b, %b], Generated = [%b], Reference = [%b]", 1024', 8'b01100001, out, 4'b1011);
            mismatch_count = mismatch_count + 1;
        end

        else begin
            $display("Test 5 passed!");
        end

        // Tick 6: Inputs = 1024', 8'b10110110, Generated = out, Reference = 4'b0101
        in = 1024'; sel = 8'b10110110; // Set input values
        #period;
        if (!(out === 4'b0101)) begin
            $display("Mismatch at index 6: Inputs = [%b, %b], Generated = [%b], Reference = [%b]", 1024', 8'b10110110, out, 4'b0101);
            mismatch_count = mismatch_count + 1;
        end

        else begin
            $display("Test 6 passed!");
        end

        // Tick 7: Inputs = 1024', 8'b11000010, Generated = out, Reference = 4'b1111
        in = 1024'; sel = 8'b11000010; // Set input values
        #period;
        if (!(out === 4'b1111)) begin
            $display("Mismatch at index 7: Inputs = [%b, %b], Generated = [%b], Reference = [%b]", 1024', 8'b11000010, out, 4'b1111);
            mismatch_count = mismatch_count + 1;
        end

        else begin
            $display("Test 7 passed!");
        end

        if (mismatch_count == 0)
            $display("All tests passed!");
        else
            $display("%0d mismatches out of %0d total tests.", mismatch_count, 8);
        $finish;
    end

endmodule